(function(jQuery){

    // ==== BEGINS PLUGGIN ====
    jQuery.fn.multislider = function(data, callback){

        // ==== CACHE DOM ====
        var jQuerymultislider = jQuery(this);
        var jQuerymsContent = jQuerymultislider.find('.MS-content');
        var jQuerymsRight = jQuerymultislider.find('button.MS-right');
        var jQuerymsLeft = jQuerymultislider.find('button.MS-left');
        var jQueryimgFirst = jQuerymsContent.find('.item:first');

        // === DETERMINE ACTION ====
        // string = method | object or nothing is to initialize
        if(typeof data === 'string'){
            getStringArgs(data);
            return jQuerymultislider;
        } else if (typeof data === 'object' || typeof data  ==='undefined'){
            init();
        }

        // ==== PLUGGIN VARIABLES ====
        var jQueryimgLast,
        totalWidth,
        numberVisibleSlides,
        animateDistance,
        animateSlideRight,
        animateSlideLeft,
        defaults,
        settings,
        animateDuration,
        autoSlideInterval;

        // = INITIALIZE =
        function init(){
            minifyContent();        // minify html
            createSettings();       // merge defaults and user provided options
            saveData();             // add data object to DOM el with reference to animation functions, allows for methods to reference at any time
            selectAnimations();     // choose default animation
        }


        // ==== EVENT HANDLERS ====
        jQuerymsRight.on('click', animateSlideLeft);
        jQuerymsLeft.on('click', animateSlideRight);
        jQuerymultislider.on('click','.MS-right, .MS-left', resetInterval);
        jQuery(window).on('resize', findItemWidth);


        // ==== FUNCTIONS (for days...) ====
        // =================================

        function pauseAbove(){
            if (window.innerWidth > settings.pauseAbove){ jQuerymultislider.addClass('ms-PAUSE'); }
            jQuery(window).on('resize',function(){
                if (window.innerWidth > settings.pauseAbove){
                    jQuerymultislider.addClass('ms-PAUSE');
                } else {
                    jQuerymultislider.removeClass('ms-PAUSE');
                }
            });
        }

        function pauseBelow(){
            if (window.innerWidth < settings.pauseBelow){ jQuerymultislider.addClass('ms-PAUSE'); }
            jQuery(window).on('resize',function(){
                if (window.innerWidth < settings.pauseBelow){
                    jQuerymultislider.addClass('ms-PAUSE');
                } else {
                    jQuerymultislider.removeClass('ms-PAUSE');
                }
            });
        }

        // used if method is called after initialization
        function getStringArgs(str){
            if (typeof jQuerymultislider.data(str) !== 'undefined'){
                jQuerymultislider.data(str)();
            } else {
                console.error("Multislider currently only accepts the following methods: next, prev, pause, play");
            }
        }

        // saves data object to DOM element
        function saveData(){
            jQuerymultislider.data({
                "pause":function(){ jQuerymultislider.addClass('ms-PAUSE'); },
                "unPause":function(){ jQuerymultislider.removeClass('ms-PAUSE'); },
                "continuous":function(){ jQuerymultislider.removeClass('ms-PAUSE'); continuousLeft(); },
                "next":function(){ overRidePause(singleLeft); },
                "nextAll":function(){ overRidePause(allLeft); },
                "prev":function(){ overRidePause(singleRight); },
                "prevAll":function(){ overRidePause(allRight); },
                "settings":settings
            });
        }

        // used when calling 'next', 'prev' methods
        function overRidePause(animation){
            if (jQuerymultislider.hasClass('ms-PAUSE')){
                jQuerymultislider.removeClass('ms-PAUSE');
                animation();
                jQuerymultislider.addClass('ms-PAUSE');
            } else {
                animation();
            }
            resetInterval();
        }

        // CRITICAL for items to be perfectly side-by-side without floating them
        function minifyContent(){
            jQuerymsContent.contents().filter(function(){
                return (this.nodeType == 3 && !/\S/.test(this.nodeValue));
            }).remove();
        }

        // updated options with defaults, measure slide widths for animation calculations, carry out setting implementations
        function createSettings() {
            defaults = settings || {
    			continuous: false,	// endless scrolling with no pauses
    			slideAll: false,	// slide all visible slides, or just one at a time
    			// autoSlide: true,	// DEPRECATED
    			interval: 2000,		// time bewteen slide animation, 0 or 'false' prevents auto-sliding
    			duration: 500,	    // duration of slide animation
    			hoverPause: true,	// pause slideshow on hover
                pauseAbove: null,   // pause above specified screen width
                pauseBelow: null    // pause below specified screen width
    		};

    		settings = jQuery.extend({},defaults,data);

            findItemWidth();
            animateDuration = settings.duration;

            if (settings.hoverPause){pauseHover();}
            // autoSlide is being depricated | Feb 2 2017
            if (settings.continuous !== true && settings.interval !== 0 && settings.interval !== false && settings.autoSlide !== false){autoSlide();}
            if (settings.pauseAbove !== null && typeof settings.pauseAbove === 'number'){ pauseAbove(); }
            if (settings.pauseBelow !== null && typeof settings.pauseBelow === 'number'){ pauseBelow(); }
        }

        // determine between single and multi-slide animations
        function selectAnimations () {
            if (settings.continuous){
                settings.autoSlide = false;
                continuousLeft();
            } else if (settings.slideAll){
                animateSlideRight = jQuerymultislider.data('prevAll');
                animateSlideLeft = jQuerymultislider.data('nextAll');
            } else {
                animateSlideRight = jQuerymultislider.data('prevAll');
                animateSlideLeft = jQuerymultislider.data('nextAll');
              //  animateSlideRight = jQuerymultislider.data('prev');
              //  animateSlideLeft = jQuerymultislider.data('next');
            }
        }

        // measure slide width, for animation calculations
        function findItemWidth(){
            reTargetSlides();
            animateDistance = jQueryimgFirst.width();
            var left = parseInt(jQuerymsContent.find('.item:first').css('padding-left'));
            var right = parseInt(jQuerymsContent.find('.item:first').css('padding-right'));
            if (left !== 0){animateDistance += left;}
            if (right !== 0){animateDistance += right;}
        }

        // recursive auto-slide loop
        function autoSlide() {
            autoSlideInterval = setInterval(function(){
                if (!jQuerymultislider.hasClass('ms-PAUSE')){
                  //  animateSlideLeft();
                }
            }, settings.interval);
        }

        function resetInterval() {
            if (settings.interval !== 0 && settings.interval !== false && settings.continuous !== true){
                clearInterval(autoSlideInterval);
                autoSlide();
            }
        }

        // target first and last visible slides before each new animation
        function reTargetSlides(){
            jQueryimgFirst = jQuerymsContent.find('.item:first');
            jQueryimgLast = jQuerymsContent.find('.item:last');
        }

        // prevent animation firing if multislider is currently animating
        // all animations pass through this function, which emits events, and adds/removes animating class
        function isItAnimating(callback){
			if(!jQuerymultislider.hasClass('ms-animating') &&
               !jQuerymultislider.hasClass('ms-HOVER') &&
               !jQuerymultislider.hasClass('ms-PAUSE')){
                    jQuerymultislider.trigger('ms.before.animate'); // event!
                    jQuerymultislider.addClass('ms-animating');
                    callback();    //callback is animation
			}
		}

        // update multislider at the end of each animation
        function doneAnimating() {
			if(jQuerymultislider.hasClass('ms-animating')){
				jQuerymultislider.removeClass('ms-animating');
                jQuerymultislider.trigger('ms.after.animate'); // event!
            }
		}

        // logic for pausing and restarting the multislider on hover
        function pauseHover() {
            // continuous scroll pause slightly different
            if(settings.continuous){
				jQuerymsContent.on('mouseover',function(){
					doneAnimating();
					jQuerymsContent.children('.item:first').stop();
				});
				jQuerymsContent.on('mouseout',function(){
					continuousLeft();
				});
			} else {
            // regular animation pausing
                jQuerymsContent.on('mouseover',function(){
                    jQuerymultislider.addClass('ms-HOVER');
                });
                jQuerymsContent.on('mouseout',function(){
                    jQuerymultislider.removeClass('ms-HOVER');
                });
			}
        }

        // calculate remaining animation, if stopped mid-animation and resuming
        function midAnimateResume(){
            animateDuration = settings.duration;
            var currentMargin = parseFloat(jQuerymsContent.find('.item:first').css("margin-left"));
            var percentageRemaining = 1-(currentMargin/-(animateDistance-1));
            animateDuration = percentageRemaining*animateDuration;
        }

        // determine how many slides need to be moved over, if slideAll is true
        function calcNumSlidesToMove(){
            totalWidth = jQuerymsContent.width();						          // total width of .MS-content containing all visible slides
		    numberVisibleSlides = Math.round(totalWidth/animateDistance);     // number of (visible) slides needed to be moved in each animation
        }


        // ==== ANIMATION FUNCTIONS ====
        // =============================
        function continuousLeft () {
            isItAnimating(function(){
                reTargetSlides();
                midAnimateResume();
                jQueryimgFirst.animate(
                    {marginLeft: -(animateDistance+1)},
                    {
                        duration: animateDuration,
                        easing: "linear",
                        complete: function(){
                            jQueryimgFirst.insertAfter(jQueryimgLast).removeAttr("style");
                            doneAnimating();
                            continuousLeft ();
                        }
                    }
                );
            });
        }

        function allLeft(){
            isItAnimating(function(){
                reTargetSlides();
                calcNumSlidesToMove();

                var jQueryclonedItemSet = jQuerymsContent.children('.item').clone();
                var filteredClones = jQueryclonedItemSet.splice(0, numberVisibleSlides);

                jQuerymsContent.append(filteredClones);

                jQueryimgFirst.animate(
                    {marginLeft: -totalWidth}, {
                        duration: animateDuration,
                        easing: "swing",
                        complete: function(){
                            jQuery(jQuerymsContent.children('.item').splice(0,numberVisibleSlides)).remove();
                            doneAnimating();
                        }
                    }
                );
            });
        }

        function allRight() {
            isItAnimating(function(){
                reTargetSlides();
                calcNumSlidesToMove();

                var numberTotalSlides = jQuerymsContent.children('.item').length;
                var jQueryclonedItemSet = jQuerymsContent.children('.item').clone();
                var filteredClones = jQueryclonedItemSet.splice(numberTotalSlides-numberVisibleSlides,numberTotalSlides);

                jQuery(jQuery(filteredClones)[0]).css('margin-left',-totalWidth); // give clone array negative margin before preppending
                jQuerymsContent.prepend(filteredClones);

                reTargetSlides();

                jQueryimgFirst.animate(
                    {
                        marginLeft: 0
                    }, {
                        duration: animateDuration,
                        easing: "swing",
                        complete: function(){
                            numberTotalSlides = jQuerymsContent.find('.item').length;
                            jQuery(jQuerymsContent.find('.item').splice(numberTotalSlides-numberVisibleSlides,numberTotalSlides)).remove();
                            jQueryimgFirst.removeAttr('style');
                            doneAnimating();
                        }
                    }
                );
            });
        }

        function singleLeft(){
            isItAnimating(function(){
                reTargetSlides();
                jQueryimgFirst.animate(
                    {
                        marginLeft: -animateDistance
                    }, {
                        duration: animateDuration,
                        easing: "swing",
                        complete: function(){
                            jQueryimgFirst.detach().removeAttr('style').appendTo(jQuerymsContent);
                            doneAnimating();
                        }
                    }
                );
            });
        }

        function singleRight(){
            isItAnimating(function(){
                reTargetSlides();
                jQueryimgLast.css('margin-left',-animateDistance).prependTo(jQuerymsContent);
                jQueryimgLast.animate(
                    {
                        marginLeft: 0
                    }, {
                        duration: animateDuration,
                        easing: "swing",
                        complete: function(){
                            jQueryimgLast.removeAttr("style");
                            doneAnimating();
                        }
                    }
                );
            });
        }
        return jQuerymultislider;
    }
})(jQuery);
